mapper.select:
SELECT e.employee_id AS employeeId, e.position_id AS positionId, e.employee_name AS employeeName, t.team_name AS teamName, d.department_name AS departmentName FROM employee e JOIN position p ON e.position_id = p.position_id LEFT JOIN team t ON e.team_id = t.team_id LEFT JOIN department d ON e.department_id = d.department_id LEFT JOIN headquarters h ON e.head_id = h.head_id WHERE p.position_name = #{positionName}
AND e.team_id = #{organizationId}
AND e.department_id = #{organizationId}
AND e.head_id = #{organizationId}
LIMIT 1
