mapper.select:
SELECT e.employee_id AS employeeId, e.position_id AS positionId, e.employee_name AS employeeName, t.team_name AS teamName, p.position_name AS positionName, d.department_name AS departmentName, r.rank_id AS rankId, r.rank_name AS rankName, r.rank_order AS rankOrder, #{type} AS type, #{lineType} AS lineType, CASE WHEN #{step} = 1 THEN '기안' ELSE '결재' END AS typeLabel FROM employee e LEFT JOIN position p ON e.position_id = p.position_id LEFT JOIN team t ON e.team_id = t.team_id LEFT JOIN department d ON e.department_id = d.department_id LEFT JOIN headquarters h ON d.head_id = h.head_id JOIN rank r ON e.rank_id = r.rank_id WHERE r.rank_order = #{rankOrder}
AND e.team_id = #{orgId}
AND e.department_id = #{orgId}
AND d.head_id = #{orgId}
LIMIT 1
