INSERT INTO introduction (
    introduction_context,
    created_at,
    introduction_type,
    department_id,
    team_id
) VALUES
      ('고사양 환경을 기반으로 몰입감 높은 게임을 기획·개발하는 부서. 그래픽, 시스템 등 다양한 분야의 전문가들이 협업해 고품질 게임을 완성합니다.', '2023-11-23', '부서', 1, NULL),
      ('게임의 시각적 완성도를 책임지는 부서로, 캐릭터, 배경, UI 등 전반적인 그래픽 요소를 제작. 게임의 콘셉트와 분위기를 시각적으로 구현해 플레이어의 몰입감을 높이는 역할을 합니다.', '2023-11-23', '부서', 2, NULL),
      ('채용, 인사 평가, 조직 운영 등 전반적인 인사 정책을 수립하고 실행하는 부서. 직원들의 성장을 지원하고 효율적인 조직 문화를 조성하는 중심적인 역할을 수행합니다.', '2023-11-23', '부서', 3, NULL),
      ('회사의 자금 흐름을 관리하고, 정확한 회계처리와 재무보고를 담당하는 부서. 예산 수립, 비용 통제, 세무 대응 등을 통해 안정적인 경영 기반을 지원합니다.', '2023-11-23', '부서', 4, NULL),
      ('자사 제품과 서비스를 고객에게 효과적으로 전달하고 매출을 창출하는 핵심 부서. 시장 분석, 고객 관리, 파트너십 구축 등을 통해 비즈니스 성장을 주도합니다.', '2023-11-23', '부서', 5, NULL),
      ('브랜드 인지도 향상과 제품 홍보를 위한 전략을 수립하고 실행하는 부서. 시장 조사, 캠페인 기획, 콘텐츠 제작 등을 통해 고객과의 효과적인 소통을 이끕니다.', '2023-11-23', '부서', 6, NULL),
      ('PC 개발팀은 다양한 장르의 PC 게임을 기획하고, 고사양 그래픽과 복잡한 시스템을 구현하는 기술 중심의 팀입니다. 엔진 개발, 클라이언트 최적화, 멀티플레이 기능 등 핵심 기능을 안정적으로 구현하며, 완성도 높은 게임 경험을 제공하기 위해 기획자, 디자이너와의 긴밀한 협업을 바탕으로 개발을 진행합니다.', '2023-11-23', '팀', NULL, 1),
      ('PC게임 기획팀은 게임의 핵심 시스템, 콘텐츠, 세계관 등을 설계하며 전체적인 플레이 흐름을 기획하는 팀입니다. 플레이어의 경험을 중심으로 레벨 디자인, 밸런스 조정, 인터페이스 흐름 등을 구체화합니다. 개발팀 및 아트팀과 협업해 기획 의도를 효과적으로 구현하고, 재미와 몰입도를 극대화합니다.', '2023-11-23', '팀', NULL, 2),
      ('아트 기획팀은 게임의 시각적 방향성과 콘셉트를 정립하고, 전체 아트 스타일의 일관성을 기획하는 팀입니다. 캐릭터, 배경, UI 등 각 요소의 아트 가이드라인을 수립하여 아티스트들이 참고할 기준을 제공합니다. 기획팀 및 개발팀과 협업해 시각적 콘셉트가 게임 전반에 잘 반영되도록 조율하는 역할을 수행합니다.', '2023-11-23', '팀', NULL, 3),
      ('아트팀은 게임의 캐릭터, 배경, 이펙트, UI 등 시각적 요소를 직접 제작하는 팀입니다. 게임 콘셉트와 기획 방향에 맞춰 다양한 아트 리소스를 구현하여 몰입감 있는 비주얼을 완성합니다. 그래픽 퀄리티와 스타일의 일관성을 유지하며 개발팀과 협업해 최종 게임 화면을 완성합니다.', '2023-11-23', '팀', NULL, 4),
      ('채용팀은 회사의 인재 확보를 위해 채용 전략을 수립하고, 전형 과정 전반을 운영하는 팀입니다. 채용 공고 작성부터 서류 심사, 면접 일정 조율, 최종 합류까지 전 과정을 체계적으로 관리합니다. 각 부서와 협업해 필요한 인재를 적시에 채용하며, 긍정적인 지원자 경험 제공에도 주력합니다.', '2023-11-23', '팀', NULL, 5),
      ('인사평가팀은 직원의 성과와 역량을 공정하게 평가하기 위한 제도를 기획하고 운영하는 팀입니다. 평가 기준 및 프로세스를 설계하고, 평가 결과를 기반으로 승진, 보상, 역량 개발에 반영합니다. 조직의 성과 향상과 개인의 성장 지원을 위한 평가 문화를 정착시키는 데 중점을 둡니다.', '2023-11-23', '팀', NULL, 6),
      ('급여 정산팀은 전사 임직원의 급여, 상여금, 수당 등을 정확하게 계산하고 지급하는 업무를 담당하는 팀입니다. 근태 정보, 세금, 4대 보험 등을 반영해 법적 기준에 맞춘 급여 산정과 정산을 수행합니다. 급여 관련 문의 대응과 더불어 연말정산, 원천징수 등 법정 신고 업무도 함께 처리합니다.', '2023-11-23', '팀', NULL, 7),
      ('세무 관리팀은 회사의 세금 신고와 납부를 정확히 수행하고, 세법에 따라 재무 리스크를 관리하는 팀입니다. 부가가치세, 법인세, 원천세 등 다양한 세목의 신고 및 절세 전략 수립을 담당합니다. 세무 조사 대응 및 회계 처리와의 연계를 통해 기업의 재무 건전성을 확보하는 역할을 합니다.', '2023-11-23', '팀', NULL, 8),
      ('콘텐츠 마케팅팀은 브랜드와 게임의 가치를 전달하기 위한 다양한 콘텐츠를 기획하고 제작하는 팀입니다. 영상, 이미지, 글 등 멀티미디어 콘텐츠를 활용해 이용자와의 소통을 강화하고 관심을 유도합니다. 게임 콘셉트와 마케팅 전략을 반영한 콘텐츠로 인지도와 팬덤 형성을 지원합니다.', '2023-11-23', '팀', NULL, 9),
      ('퍼포먼스 팀은 데이터 기반의 광고 운영을 통해 효율적인 마케팅 성과를 창출하는 팀입니다. 검색, 디스플레이, 소셜 미디어 광고 등을 집행하며, 주요 지표(ROAS, CPC 등)를 지속적으로 분석·최적화합니다. 캠페인 성과를 정량적으로 평가하여 예산 집행의 효율성과 전환율을 극대화합니다.', '2023-11-23', '팀', NULL, 10),
      ('B2B 영업팀은 기업 고객을 대상으로 자사 제품과 서비스를 제안하고 계약을 성사시키는 팀입니다. 고객사의 니즈를 분석하고 맞춤형 솔루션을 제시하여 장기적인 파트너십을 구축합니다. 시장 동향과 경쟁사를 분석해 전략적인 영업 기회를 발굴하고 매출 성장을 이끕니다.', '2023-11-23', '팀', NULL, 11),
      ('B2C 영업팀은 일반 소비자를 대상으로 자사 제품과 서비스를 직접 소개하고 판매하는 팀입니다. 고객의 구매 여정을 분석하고 효과적인 프로모션 및 판매 전략을 기획해 구매 전환을 유도합니다. 고객 만족도와 충성도를 높이기 위한 피드백 수집 및 사후 관리도 함께 수행합니다.', '2023-11-23', '팀', NULL, 12);

